    const [state, setState] = useState({
        currentQuestionIndex: 0,
        questions: []
    });
    const testClick = () => {
        console.log('testClick');
       setState(currentQuestionIndex + 1);
    }
    return (
        <div className={'quiz'}>
            <div>
                <div className={'score'}>
                    Question 1/8
                </div>
            </div>

            <Question/>

            <div className={'next-button'} onClick={testClick}>
                Next question {state.currentQuestionIndex}
            </div>
        </div>
    )
};

export default Quiz
